<template>
    <div class="row">
        <div class="col-md-4 mb-4" v-for="image in images" :key="image">
            <img :src="image" class="img-fluid" @click="openModal(image)" style="cursor: pointer;" />
        </div>

        <!-- Modal -->
        <div class="modal fade" id="galleryModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div class="modal-dialog">
                <div class="modal-content">
                    <div class="modal-header">
                        <h5 class="modal-title" id="exampleModalLabel">Image</h5>
                        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                    </div>
                        <div class="modal-body text-cent">
                        <img :src="selectedImage" class="img-fluid" />
                    </div>
                </div>
            </div>
        </div>
    </div>
</template>

<script>
import "bootstrap/dist/css/bootstrap.min.css";
import * as bootstrap from "bootstrap";

export default {
    data() {
        return {
            images: ['data:image/jpeg;base64,',
             'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTxQ77O_HlrRoPBpiz3Xc8m9wqXKNNF9Q9VKQ&s',
              'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQE9ngJvuTofI6s7-rypiKicIpL4Z-X9jNVLA&s'],
            selectedImage: null,
        };
    },
    methods: {
        openModal(image) {
            this.selectedImage = image;
            const modal = new bootstrap.Modal(document.getElementById('galleryModal'));
            modal.show();
        }
    }
};
</script>
  